import React, { useState, useEffect } from 'react';
import './ViewUserPage.css';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const ViewUserPage = ({ onSave, onCancel }) => {
  const { associate_id } = useParams();
  const [viewUser, setViewUser] = useState({
    associate_id: '',
    associate_name: '',
    localsystemid: '',
    email: '',
    password: '',
    manager_id: '',
    manager_name: '',
    manager_email: '',
    company: '',
    OpCo: ''
  });

  useEffect(() => {
    axios.get(`http://localhost:3000/admin/get-user/${associate_id}`)
      .then(response => {
        console.log(response);
        const userData = response.data.dta;
        setViewUser(userData);
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
      });
  }, [associate_id]);
  

  const handleChange = (e) => {
    const { name, value } = e.target;
    setViewUser(prevUser => ({
      ...prevUser,
      [name]: value
    }));
  };

  const handleSave = () => {
    axios.patch(`http://localhost:3000/admin/edit-user/${viewUser.associate_id}`, viewUser)
      .then(response => {
        onSave(viewUser);
      })
      .catch(error => {
        console.error("Error updating user data:", error);
      });
  };

  return (
    <div className="view-user-container">
      <div className="id-card">
        <div className="id-card-section">
          <div className="field">
            <label>Associate ID:</label>
            <input type="text" name="associate_id" value={viewUser.associate_id} onChange={handleChange} readOnly />
          </div>
          <div className="field">
            <label>Associate Name:</label>
            <input type="text" name="associate_name" value={viewUser.associate_name} onChange={handleChange} />
          </div>
          <div className="field">
            <label>Local System ID:</label>
            <input type="text" name="localsystemid" value={viewUser.localsystemid} onChange={handleChange} />
          </div>
          <div className="field">
            <label>Email:</label>
            <input type="email" name="email" value={viewUser.email} onChange={handleChange} readOnly />
          </div>
          <div className="field">
            <label>Password:</label>
            <input type="password" name="password" value={viewUser.password} onChange={handleChange} />
          </div>
        </div>
        <div className="id-card-section">
          <div className="field">
            <label>Manager ID:</label>
            <input type="text" name="manager_id" value={viewUser.manager_id} onChange={handleChange} />
          </div>
          <div className="field">
            <label>Manager Name:</label>
            <input type="text" name="manager_name" value={viewUser.manager_name} onChange={handleChange} />
          </div>
          <div className="field">
            <label>Manager Email:</label>
            <input type="email" name="manager_email" value={viewUser.manager_email} onChange={handleChange} />
          </div>
          <div className="field">
            <label>Company:</label>
            <input type="text" name="company" value={viewUser.company} onChange={handleChange} />
          </div>
          <div className="field">
            <label>OpCo:</label>
            <input type="text" name="OpCo" value={viewUser.OpCo} onChange={handleChange} />
          </div>
        </div>
      </div>
      <div className="buttons">
        <button type="button" onClick={handleSave}>Save</button>
        <button type="button" onClick={onCancel}>Cancel</button>
      </div>
    </div>
  );
};

export default ViewUserPage;
