import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import "./Home.css";

const scrollToSection = (sectionId) => {
  const section = document.getElementById(sectionId);
  if (section) {
    window.scrollTo({
      top: section.offsetTop,
      behavior: "smooth",
    });
  }
};

const HeroSection = () => {
  return (
    <header className="hero">
      <div className="hero-content">
        <h1 className="hero-title">Your Comfort Awaits, Reserve Now</h1>
        <p className="hero-subtitle">
          Make seat reservations simple and straightforward.
        </p>
        <div className="hero-button">
          <Link to="./bookyourseat" className="home-btn-text">
            Book Your Seat
          </Link>
        </div>
        <div
          className="arrow-container"
          onClick={() => scrollToSection("full")}
        >
          <div className="arrow"></div>
        </div>
      </div>
    </header>
  );
};

const Home = () => {
  useEffect(() => {
    // Initialize smooth scroll when the component mounts
    smoothScroll.init();
  }, []);

  return (
    <div className="content whole-body-home">
      <HeroSection />
      <div id="full" className="full-section"></div>
    </div>
  );
};

export default Home;
