import React, { useState } from "react";
import { Link, useLocation } from "react-router-dom";
import {
  AppBar,
  Button,
  Hidden,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  makeStyles,
  Menu,
  MenuItem,
  Toolbar,
} from "@material-ui/core";
import clsx from "clsx";
import MenuIcon from "@material-ui/icons/Menu";
import  HomeIcon  from "@material-ui/icons";
import DashboardOutlinedIcon from '@material-ui/icons/DashboardOutlined';
import PeopleAltIcon from '@material-ui/icons/PeopleAlt';
import EventSeatIcon from '@material-ui/icons/EventSeat';

const useStyles = makeStyles((theme) => ({
  root: {
    boxShadow: "none",
  },
  toolbar: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  },
  listItem: {
    marginRight: theme.spacing(3),
    color: "inherit",
    textDecoration: "none",
    display: "flex",
    alignItems: "center",
  },
}));

function TopBar(props) {
  const { className, openMenu, ...rest } = props;
  const classes = useStyles();
  const [anchorEl, setAnchorEl] = useState(null);
  const location = useLocation();
  const auth = useSelector(state => state.auth);

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const handleMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  return (
    <AppBar {...rest} className={clsx(classes.root, className)} color="primary">
      <Toolbar className={classes.toolbar}>
        <ListItem
          button
          component={Link}
          to="/"
          className={classes.listItem}
          selected={location.pathname === "/"}
        >
          <ListItemIcon>
            <DashboardOutlinedIcon />
          </ListItemIcon>
          <ListItemText primary="React Admin Panel Dashboard" />
        </ListItem>
        <Hidden mdDown>
          <List component="nav" aria-labelledby="main navigation" className={classes.list}>
            <ListItem
              button
              component={Link}
              to="/table"
              className={classes.listItem}
              selected={location.pathname === "/table"}
            >
              <ListItemIcon>
                <HomeIcon />
              </ListItemIcon>
              <ListItemText primary="Table" />
            </ListItem>
            <ListItem
              button
              component={Link}
              to="/admin/usermanagement"
              className={classes.listItem}
              selected={location.pathname === "/admin/usermanagement"}
            >
              <ListItemIcon>
                <PeopleAltIcon />
              </ListItemIcon>
              <ListItemText primary="Manage User" />
            </ListItem>
            <ListItem
              button
              component={Link}
              to="/bookyourseat"
              className={classes.listItem}
              selected={location.pathname === "/bookyourseat"}
            >
              <ListItemIcon>
                <EventSeatIcon />
              </ListItemIcon>
              <ListItemText primary="Book Your Seat" />
            </ListItem>
          </List>
        </Hidden>
        <Hidden lgUp>
          <MenuIcon onClick={openMenu} />
        </Hidden>
        <Button color="inherit" onClick={handleMenuOpen}>
          {auth.user}
        </Button>
        <Menu open={Boolean(anchorEl)} onClose={handleMenuClose} anchorEl={anchorEl}>
          <MenuItem>Profile</MenuItem>
          <MenuItem>
            <Link to="/auth/login" onClick={()=>{
              localStorage.removeItem("user");
            }}> Logout</Link>
          </MenuItem>
        </Menu>
      </Toolbar>
    </AppBar>
  );
}

export default TopBar;
