import {
  AppBar,
  Hidden,
  Button,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  makeStyles,
  Menu,
  MenuItem,
  Toolbar,
  Typography,
} from "@material-ui/core";
import clsx from "clsx";
import React, { useState } from "react";
import { Link, useLocation } from "react-router-dom";
import MenuIcon from "@material-ui/icons/Menu";
import DashboardOutlinedIcon from '@material-ui/icons/DashboardOutlined';
import PeopleAltIcon from '@material-ui/icons/PeopleAlt';
import EventSeatIcon from '@material-ui/icons/EventSeat';
import HomeIcon from '@material-ui/icons/Home';
import { useSelector } from "react-redux";

const useStyles = makeStyles({
  root: {
    boxShadow: "none",
  },
  flexGrow: {
    flexGrow: 1,
  },
});

function TopBar(props) {
  const { className, openMenu, ...rest } = props;
  const classes = useStyles();

  const [anchorEl, setAnchorEl] = useState(null);
  const location = useLocation(); // Use useLocation hook to get the current location
  const auth = useSelector(state => state.auth);

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const handleMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  return (
    <AppBar {...rest} className={clsx(classes.root, className)} color="primary">
      <Toolbar>
        <ListItem button component={Link} to="/">
          <ListItemIcon>
            <DashboardOutlinedIcon />
          </ListItemIcon>
          <ListItemText>Dashboard</ListItemText>
        <Typography variant="h6" className={classes.flexGrow}>
          React Admin Panel
        </Typography>
        </ListItem>
        <Hidden mdDown>
          <List component="nav" aria-labelledby="main navigation">
            <ListItem button component={Link} to="/table" selected={location.pathname === "/table"}>
              {/* <ListItemIcon>
                <HomeIcon />
              </ListItemIcon> */}
              <ListItemText primary="Table" />
            </ListItem>
            <ListItem button component={Link} to="/admin/usermanagement" selected={location.pathname === "/admin/usermanagement"}>
              {/* <ListItemIcon>
                <PeopleAltIcon />
              </ListItemIcon> */}
              <ListItemText primary="Manage User" />
            </ListItem>
            <ListItem button component={Link} to="/bookyourseat" selected={location.pathname === "/bookyourseat"}>
              {/* <ListItemIcon>
                <EventSeatIcon />
              </ListItemIcon> */}
              <ListItemText primary="Book Your Seat" />
            </ListItem>
          </List>
        </Hidden>
        <Hidden lgUp>
          <MenuIcon onClick={openMenu} />
        </Hidden>
        <Button color="inherit" onClick={handleMenuOpen}>
          {auth.user}
        </Button>
        <Menu open={Boolean(anchorEl)} onClose={handleMenuClose} anchorEl={anchorEl}>
          <MenuItem>Profile</MenuItem>
          <MenuItem><Link to="/auth/login" onClick={()=>{
            localStorage.removeItem("user");
          }}> Logout</Link></MenuItem>
        </Menu>
      </Toolbar>
    </AppBar>
  );
}

export default TopBar;
