import React from 'react';
import './Home.css';
import { Link } from "react-router-dom"; // Import Link from react-router-dom

const HeroSection = () => {
  return (
    <header className="hero">
      <div className="hero-content">
        <h1 className="hero-title">Your Animated Text Goes Here</h1>
        <p className="hero-subtitle">A brief description of your content</p>
        <div className="hero-button">
        <Link to="./bookyourseat">Book Your Seat</Link>
      </div>
      </div>
    </header>
  );
};

const Home = () => {
  return (
    <div className="content whole-body-home">
      <HeroSection />
    </div>
  );
};

export default Home;
