import React from "react";
import { Link } from "react-router-dom";
import "./Home.css";

const HeroSection = () => {
  return (
    <header className="hero">
      <div className="hero-content">
        <h1 className="hero-title">Your Comfort Awaits, Reserve Now</h1>
        <p className="hero-subtitle">
          Make seat reservations simple and straightforward.
        </p>
        <div className="hero-button">
          <Link to="./bookyourseat" className="home-btn-text">
            Book Your Seat
          </Link>
        </div>
        <div id="wrapper">
          <div id="wrapper-inner">
            <div id="scroll-down">
              <span class="arrow-down"></span>
              <span id="scroll-title">Scroll down</span>
            </div>
          </div>
        </div>
      </div>
    </header>
  );
};

const Home = () => {
  return (
    <div className="content whole-body-home">
      <HeroSection />
    </div>
  );
};

export default Home;
