import React, { useState, useEffect } from 'react';
import './ViewUserPage.css';
import axios from 'axios';
import { useParams } from 'react-router-dom'; // Import useParams hook from react-router-dom

const ViewUserPage = ({ onSave, onCancel }) => {
  const { id } = useParams(); // Use useParams hook to access parameters from the URL

  const [user, setUser] = useState({
    associate_id: '',
    associate_name: '',
    email: ''
  });

  useEffect(() => {
    // Fetch user details from the server based on associate_id
    axios.get(`http://localhost:3000/admin/get-user/${associate_id}`)
      .then(response => {
        setUser(response.data); // Update user state with fetched data
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
      });
  }, [id]); // Update the useEffect dependency to include id

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser(prevUser => ({
      ...prevUser,
      [name]: value
    }));
  };

  const handleSave = () => {
    // Make PUT request to save edited user data
    axios.patch(`http://localhost:3000/admin/edit-user/${user.associate_id}`, user)
      .then(response => {
        // Handle successful save
        console.log("User data updated successfully:", response.data);
        onSave(user); // Pass the updated user data to the parent component
      })
      .catch(error => {
        // Handle error
        console.error("Error updating user data:", error);
      });
  };

  return (
    <div className="edit-user-popup">
      <h2>View User</h2>
      <form>
        <label>
          Associate ID:
          <input type="text" name="associate_id" value={user.associate_id} onChange={handleChange} readOnly />
        </label>
        <label>
          Associate Name:
          <input type="text" name="associate_name" value={user.associate_name} onChange={handleChange} />
        </label>
        <label>
          Email:
          <input type="email" name="email" value={user.email} onChange={handleChange} readOnly />
        </label>
        <div className="buttons">
          <button type="button" onClick={handleSave}>Save</button>
          <button type="button" onClick={onCancel}>Cancel</button>
        </div>
      </form>
    </div>
  );
};

export default ViewUserPage;
