import React, { useState, useEffect } from 'react';
import './ViewUserPage.css';
import axios from 'axios';
import { useParams } from 'react-router-dom'; // Import useParams hook from react-router-dom

const ViewUserPage = ({ onSave, onCancel }) => {
  const { associate_id } = useParams(); // Use useParams hook to access parameters from the URL

  const [viewUser, setViewUser] = useState();

  useEffect(() => {
    // Fetch user details from the server based on associate_id
    axios.get(`http://localhost:3000/admin/get-user/${associate_id}`)
      .then(response => {
        console.log("Fetched user data:", response.data); 
        setViewUser(response.data);
        console.log(viewUser);
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
      });
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setViewUser(prevUser => ({
      ...prevUser,
      [name]: value
    }));
  };

  const handleSave = () => {
    // Make PATCH request to save edited user data
    axios.patch(`http://localhost:3000/admin/edit-user/${viewUser.associate_id}`, viewUser)
      .then(response => {
        console.log("User data updated successfully:", response.data); // Log successful update message
        onSave(viewUser); // Pass the updated user data to the parent component
      })
      .catch(error => {
        console.error("Error updating user data:", error); // Log error message
      });
  };

  return (
    <div className="edit-user-popup">
      <h2>View User</h2>
      <form>
        <label>
          Associate ID:
          <input type="text" name="associate_id" value={viewUser.associate_id} onChange={handleChange} readOnly />
        </label>
        <label>
          Associate Name:
          <input type="text" name="associate_name" value={viewUser.associate_name} onChange={handleChange} />
        </label>
        <label>
          Email:
          <input type="email" name="email" value={viewUser.email} onChange={handleChange} readOnly />
        </label>
        <div className="buttons">
          <button type="button" onClick={handleSave}>Save</button>
          <button type="button" onClick={onCancel}>Cancel</button>
        </div>
      </form>
    </div>
  );
};

export default ViewUserPage;
