import React, { useState, useEffect } from "react";
import {
  AppBar,
  Hidden,
  IconButton,
  makeStyles,
  Menu,
  MenuItem,
  Toolbar,
  Typography,
} from "@material-ui/core";
import clsx from "clsx";
import { Link } from "react-router-dom";
import MenuIcon from "@material-ui/icons/Menu";
import DashboardOutlinedIcon from '@material-ui/icons/DashboardOutlined';
import PeopleAltIcon from '@material-ui/icons/PeopleAlt';
import EventSeatIcon from '@material-ui/icons/EventSeat';
import HomeIcon from '@material-ui/icons/Home';
import { useSelector } from "react-redux";

const useStyles = makeStyles({
  root: {
    boxShadow: "none",
  },
  flexGrow: {
    flexGrow: 1,
  },
});

function TopBar(props) {
  const { className, openMenu, ...rest } = props;
  const classes = useStyles();

  const [anchorEl, setAnchorEl] = useState(null);
  const [userName, setUserName] = useState('');

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (user) {
      setUserName(user.name);
    }
  }, []);

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const handleMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const auth = useSelector(state => state.auth);

  return (
    <AppBar {...rest} className={clsx(classes.root, className)} color="primary">
      <Toolbar>
        <IconButton color="inherit" component={Link} to="/">
          <DashboardOutlinedIcon />
        </IconButton>
        <Typography variant="h5" color="inherit" style={{ paddingLeft: '10px' }}>Book Your Seat</Typography>
        <div className={classes.flexGrow} />
        <IconButton color="inherit" onClick={handleMenuOpen}>
          {userName} {/* Display user name */}
        </IconButton>
        <Menu open={Boolean(anchorEl)} onClose={handleMenuClose} anchorEl={anchorEl}>
          <MenuItem>Profile</MenuItem>
          <MenuItem><Link to="/auth/login" onClick={()=>{
            localStorage.removeItem("user");
          }}> Logout</Link></MenuItem>
        </Menu>
        <Hidden lgUp>
          <IconButton color="inherit" onClick={openMenu}>
            <MenuIcon />
          </IconButton>
        </Hidden>
        <Hidden mdDown>
          <IconButton color="inherit" component={Link} to="/">
            <HomeIcon />
          </IconButton>
          <IconButton color="inherit" component={Link} to="/admin/usermanagement">
            <PeopleAltIcon />
          </IconButton>
          <IconButton color="inherit" component={Link} to="/bookyourseat">
            <EventSeatIcon />
          </IconButton>
        </Hidden>
      </Toolbar>
    </AppBar>
  );
}

export default TopBar;
