import React from "react";
import { Link } from "react-router-dom";
import "./Home.css";

const scrollToSection = (id) => {
  const section = document.getElementById(id);
  if (section) {
    section.scrollIntoView({
      behavior: "smooth",
      block: "start",
    });
  }
};

const HeroSection = () => {
  return (
    <header className="hero">
      <div className="hero-content">
        <h1 className="hero-title">Your Comfort Awaits, Reserve Now</h1>
        <p className="hero-subtitle">
          Make seat reservations simple and straightforward.
        </p>
        <div className="hero-button">
          <Link to="./bookyourseat" className="home-btn-text">
            Book Your Seat
          </Link>
        </div>
        <div
          className="arrow-container"
          onClick={() => scrollToSection("full")}
        >
          <div className="arrow"></div>
        </div>
      </div>
    </header>
  );
};

const Home = () => {
  return (
    <div className="content whole-body-home">
      <HeroSection />
      <div id="full" className="full-section"></div>
    </div>
  );
};

export default Home;
