import {
  AppBar,
  Button,
  Hidden,
  IconButton,
  makeStyles,
  Menu,
  MenuItem,
  Toolbar,
  Typography,
} from "@material-ui/core";
import clsx from "clsx";
import React, { useState } from "react";
import { Link } from "react-router-dom";
import MenuIcon from "@material-ui/icons/Menu";
import DashboardOutlinedIcon from '@material-ui/icons/DashboardOutlined';
import { useSelector } from "react-redux";
import Home from "@material-ui/icons/Home";
import AppsIcon from "@material-ui/icons/Apps";
import PeopleAltIcon from '@material-ui/icons/PeopleAlt';
// import EventSeatIcon from '@material-ui/icons/EventSeat';

const useStyles = makeStyles({
  root: {
    boxShadow: "none",
  },
  flexGrow: {
    flexGrow: 1,
  },
});

function TopBar(props) {
  const { className, openMenu, ...rest } = props;
  const classes = useStyles();

  const [anchorEl, setAnchorEl] = useState(null);

  const handleMenuClose = () => {
      setAnchorEl(null);
  };
  const handleMenuOpen = (event) => {
      setAnchorEl(event.currentTarget);
  };

  const auth = useSelector(state => state.auth);

  return (
    <AppBar {...rest} className={clsx(classes.root, className)} color="primary">
      <Toolbar>
        <IconButton color="inherit" component={Link} to="/">
          <DashboardOutlinedIcon />
        </IconButton>
        <Typography variant="h5" color="inherit" style={{ paddingLeft: '10px' }}>React Admin Panel</Typography>
        <div className={classes.flexGrow} />
        <Hidden lgUp>
          <IconButton color="inherit" onClick={openMenu}>
            <MenuIcon />
          </IconButton>
        </Hidden>
        <Hidden mdDown>
          <Button color="inherit" component={Link} to="/">
            <Home />
          </Button>

          


          {/* <Button color="inherit" component={Link} to="/table">
            <AppsIcon />
          </Button> */}
          <Button color="inherit" component={Link} to="/admin/usermanagement">
            <PeopleAltIcon />
          </Button>
        <Button color="inherit" variant="outlined" onClick={handleMenuOpen}>
          {auth.user}
        </Button>
          {/* <Button color="inherit" component={Link} to="/bookyourseat">
            <EventSeatIcon />
          </Button> */}
        <Menu open={Boolean(anchorEl)} onClose={handleMenuClose} anchorEl={anchorEl}>
          <MenuItem>Profile</MenuItem>
          <MenuItem><Link to="/auth/login" onClick={()=>{
            localStorage.removeItem("user");
          }}> Logout</Link></MenuItem>
        </Menu>
        </Hidden>
      </Toolbar>
    </AppBar>
  );
}

export default TopBar;
