import {
  AppBar,
  Hidden,
  IconButton,
  makeStyles,
  Menu,
  MenuItem,
  Toolbar,
  Typography,
} from "@material-ui/core";
import clsx from "clsx";
import React, { useState } from "react";
import { Link } from "react-router-dom";
import MenuIcon from "@material-ui/icons/Menu";
// import DashboardOutlinedIcon from '@material-ui/icons/DashboardOutlined';
import PeopleAltIcon from '@material-ui/icons/PeopleAlt';
import EventSeatIcon from '@material-ui/icons/EventSeat';
import HomeIcon from '@material-ui/icons/Home';
import { useSelector } from "react-redux";

const useStyles = makeStyles({
  root: {
    boxShadow: "none",
  },
  flexGrow: {
    flexGrow: 1,
  },
});

function TopBar(props) {
  const { className, openMenu, ...rest } = props;
  const classes = useStyles();

  const [anchorEl, setAnchorEl] = useState(null);

  const handleMenuClose = () => {
      setAnchorEl(null);
  };
  const handleMenuOpen = (event) => {
      setAnchorEl(event.currentTarget);
  };

  const auth = useSelector(state => state.auth);

  return (
    <AppBar {...rest} className={clsx(classes.root, className)} color="primary">
      <Toolbar>
        <IconButton color="inherit" component={Link} to="/">
        <img src="./Veralto-logo.png" className="Veralto-logo" style={{ width: "100px", height: "auto" }} />
        </IconButton>
        <Typography variant="h5" color="inherit" style={{ paddingLeft: '10px' }}>Book Your Seat</Typography>
        <div className={classes.flexGrow} />
        <Menu open={Boolean(anchorEl)} onClose={handleMenuClose} anchorEl={anchorEl}>
          <MenuItem>Profile</MenuItem>
          <MenuItem><Link to="/auth/login" onClick={()=>{
            localStorage.removeItem("user");
          }}> Logout</Link></MenuItem>
        </Menu>
        <Hidden lgUp>
          <IconButton color="inherit" onClick={openMenu}>
            <MenuIcon />
          </IconButton>
        </Hidden>
        <Hidden mdDown>
          <IconButton color="inherit" component={Link} to="/">
            <HomeIcon />
          </IconButton>
          {/* <IconButton color="inherit" component={Link} to="/table">
            <AppsIcon />
          </IconButton> */}
          <IconButton color="inherit" component={Link} to="/admin/usermanagement">
            <PeopleAltIcon />
          </IconButton>
          <IconButton color="inherit" component={Link} to="/bookyourseat">
            <EventSeatIcon />
          </IconButton>
          <IconButton color="inherit" onClick={handleMenuOpen}>
            {auth.user}
          </IconButton>
        </Hidden>
      </Toolbar>
    </AppBar>
  );
}

export default TopBar;
