import React from "react";
import "./Home.css";
import { Link } from "react-router-dom"; // Import Link from react-router-dom

const HeroSection = () => {
  return (
    <header className="hero">
      <div className="hero-content">
        <h1 className="hero-title">Your Comfort Awaits, Reserve Now</h1>
        <p className="hero-subtitle">
          Make seat reservations simple and straightforward.
        </p>
        <div className="hero-button">
          <Link to="./bookyourseat" className="home-btn-text">
            Book Your Seat
          </Link>
        </div>
        <div class="arrow-container">
  <a href="#extra-info" class="hide-on-palm arrow-down">
    <span class="fa-stack fa-3x">
            <i class="fa fa-circle fa-stack-2x"></i>
            <i class="fa fa-chevron-down fa-stack-1x"></i>
          </span>
  </a>
  </div>
      </div>
    </header>
  );
};

const Home = () => {
  return (
    <div className="content whole-body-home">
      <HeroSection />
    </div>
  );
};


export default Home;
