import React from 'react';
import './Home.css';
import { Link } from "react-router-dom"; // Import Link from react-router-dom

const HeroSection = () => {
  return (
    <header className="hero">
      <div className="hero-content">
        <h1 className="hero-title">Your Comfort Awaits, Reserve Now</h1>
        <p className="hero-subtitle">Make seat reservations simple and straightforward.</p>
        <div className="hero-button">
        <Link to="./bookyourseat" className="home-btn-text">Book Your Seat</Link>
        <div class="arrow-container">
          <a data-scroll href="#full">
    <div class="arrow"></div>
  </a>
</div>
      </div>
      </div>
    </header>
  );
};

const Home = () => {
  return (
    <div className="content whole-body-home">
      <HeroSection />
    </div>
  );
};

export default Home;
