import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import { Element, scroller } from "react-scroll"; // Import Element and scroller from react-scroll
import "./Home.css";

const HeroSection = () => {
  const scrollToSection = () => {
    scroller.scrollTo("full", {
      duration: 800,
      delay: 0,
      smooth: "easeInOutQuart",
    });
  };

  return (
    <header className="hero">
      <div className="hero-content">
        <h1 className="hero-title">Your Comfort Awaits, Reserve Now</h1>
        <p className="hero-subtitle">
          Make seat reservations simple and straightforward.
        </p>
        <div className="hero-button">
          <Link to="./bookyourseat" className="home-btn-text">
            Book Your Seat
          </Link>
        </div>
        <div className="arrow-container" onClick={scrollToSection}>
          <div className="arrow"></div>
        </div>
      </div>
    </header>
  );
};

const Home = () => {
  useEffect(() => {
    // Initialize smooth scroll when the component mounts
    smoothScroll.init();
  }, []);

  return (
    <div className="content whole-body-home">
      <HeroSection />
      <Element name="full" className="full-section"></Element>
    </div>
  );
};

export default Home;
